$(document).ready(function() {
	
	
	
	
	
		$('.login_button, .watch, .order, .readmore, .download,.request').append('<span class="hover"></span>').each(function () {
	  		var $span = $('> span.hover', this).css('opacity', 0);
	  		$(this).hover(function () {
	    		$span.stop().fadeTo(500, 1);
	 		}, function () {
	   	$span.stop().fadeTo(500, 0);
	  		});
		});
		
		
		 $('.login_button').click(function() {
      $('#myModal').reveal();
    });
		
		
		
		$('ul.sf-menu').superfish({
				delay:         50,
				speed:         100,
				animation:     {opacity:'show'},
				autoArrows:    true, 
				dropShadows:   true,   
				});
		
		
			$('#layerslider').layerSlider({
		
		
	autoStart               : true,
    firstLayer              : 0,
    twoWaySlideshow         : true,
    keybNav                 : true,
    touchNav                : true,
    imgPreload              : true,
    navPrevNext             : true,
    navStartStop            : true,
    navButtons              : true,
    skin                    : 'my',
    skinsPath               : 'css/skins/',
    pauseOnHover            : true,
    globalBGColor           : 'transparent',
    globalBGImage           : 'img/gallery_bg.png',
    animateFirstLayer       : true,
    yourLogo                : false,
    yourLogoStyle           : 'position: absolute; z-index: 1001; left: 10px; top: 10px;',
    yourLogoLink            : false,
    yourLogoTarget          : '_blank',
    loops                   : 0,
    forceLoopNum            : true,
    autoPlayVideos          : false,
    autoPauseSlideshow      : 'auto',
	});
		
	$('#mini_slider').nivoSlider({
			
			effect: 'fade',
			directionNav: true, // Next & Prev navigation
        	directionNavHide: false, // Only show on hover
       	 	controlNav: true, // 1,2,3... navigation
        	controlNavThumbs: true,
			pauseOnHover: true,
    		controlNavThumbsFromRel:true,
			
			
			});	
		
		$(".top_info a, .top_call a, .note a").tipTip({
	defaultPosition: 'top',
	delay: 50,
	fadeIn: 300,
	fadeOut: 300,
	
	
	});
		
		
		$(".watch").colorbox({iframe:true, innerWidth:700, innerHeight:393});
		
		$('input, textarea,').placeholder();
		
});


